import { Component } from '@angular/core';
import { Producto } from '../producto';
import { ProductoService } from '../producto.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-agregar-producto',
  templateUrl: './agregar-producto.component.html',
  styleUrls: ['./agregar-producto.component.css']
})
export class AgregarProductoComponent {
  producto: Producto = new Producto();

  constructor(private productoServicio: ProductoService,
    private enrutador: Router){}

    onSubmit(){
      this.guardarProducto();
    }

    guardarProducto(){
      this.productoServicio.agregarProducto(this.producto).subscribe(
        {
          next: (datos) => {
            this.irListaProductos();

          },
          error: (error:any) =>{console.log(error)}
        }
      );
    }

    irListaProductos(){
      this.enrutador.navigate(['/productos']);
    }

}
<div class="container">
    <div class="container text-center" style="margin: 30px">
    <h3>Agregar Producto</h3>
    </div>
</div>
<form (ngSubmit)="onSubmit()">
    <div class="mb-3">
      <label for="descripcion" class="form-label">Descripcion del Producto
      </label>
      <input type="text" class="form-control" 
      id="descripcion" name="descripcion" required="true"
      [(ngModel)]="producto.descripcion">
    </div>


    <div class="mb-3">
      <label for="precio" class="form-label">Precio</label>
      <input type="number" step="any" class="form-control" id="precio"
      name="precio"
      [(ngModel)]="producto.precio">
    </div>

    <div class="mb-3">

        <label for="existencia" class="form-label" >Existencia
        </label>
        <input  type="number"  class="form-control" id="existencia"
        [(ngModel)]="producto.existencia">
    </div>

    <div class="text-center" style="margin: 10px">
      <button type="submit" class="btn btn-warning btn-sm me-3">Agregar</button>
      <button routerLink="/productos" class="btn btn-danger btn-sm">Regresar</button>
     </div>
